    private void CadSalvarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_CadSalvarActionPerformed
    
    Cadastro objcadastro = new Cadastro();
    
    objcadastro.setNome(CadNome.getText());
    objcadastro.setEndereco(CadEndereco.getText());
    objcadastro.setBairro(CadBairro.getText());
    objcadastro.setCidade(CadCidade.getText());
    objcadastro.setEstado((String) CadEstado.getSelectedItem());
    
    limpar();
    
    conecxao conect = new conecxao();
    
    
    
    JOptionPane.showMessageDialog(null,"O Cadastro Foi Salvo com Sucesso" );
    

        
        
        
        
        
        // TODO add your handling code here:
    }//GEN-LAST:event_CadSalvarActionPerformed

    public void limpar(){
        CadNome.setText("");
        CadEndereco.setText("");
        CadBairro.setText("");
        CadCidade.setText("");
        CadEstado.setSelectedIndex(-1);
        
       
        
    }
    
    
